import type { HasBackgroundColor, PixelAccessor } from "../pixel-accessor";
import type { Color } from "../types";
/**
 * Color interpolator interface
 */
export interface ColorInterpolator {
    /**
     * Interpolates pixel color at given pixel coordinates
     *
     * @param image
     * @param x
     * @param y
     */
    interpolate(image: PixelAccessor & HasBackgroundColor, x: number, y: number): Color;
}
