import type { ColorResampler } from "./ColorResampler";
/**
 * Color resampler with support of Elliptical Weighted Average technique.
 */
export interface EwaColorResampler extends ColorResampler {
    /**
     * Returns weight lookup table used by resampler.
     */
    getWeightLookupTable(): number[];
}
/**
 * Checks if given argument implements EwaColorResampler interface.
 *
 * @param candidate
 */
export declare function isEwaColorResampler(candidate: unknown): candidate is EwaColorResampler;
