import type { ViewportLiteral } from "../pixel-accessor";
import type { Point } from "../types";
import type { DistortionProcessor } from "./DistortionProcessor";
/**
 * DistortionProcessorFactory options.
 */
export interface DistortionProcessorFactoryOptions {
    /**
     * When set to true, image's virtual viewport offset will be set to (0, 0) after distortion. When set to array [x, y],
     * corresponding x and y values will be added to image virtual vieport offset.
     * When set to false, viewport offset stays untouched.
     * @default false
     */
    repage?: boolean | Point | ViewportLiteral;
    /**
     * Output scaling factor for supersampling. Must be >= 1.
     * @default 1
     */
    outputScaling?: number;
    /**
     * Time in milliseconds after which further pixel processing will be delayed.
     * @default 50
     */
    asyncTimeout?: number;
    /**
     * AbortSignal for distortion aborting.
     */
    abortSignal?: AbortSignal;
}
/**
 * DistortionProcessor factory interface.
 */
export interface DistortionProcessorFactory {
    /**
     * Creates distortion processor.
     *
     * @param options
     */
    create(options?: DistortionProcessorFactoryOptions): DistortionProcessor;
}
