import type { ImageAdapter } from "./ImageAdapter";
/**
 * ImageAdapter factory interface.
 */
export interface ImageAdapterFactory<ResourceType> {
    /**
     * Returns true if factory can create image adapter for given resource.
     *
     * @param resource
     */
    match(resource: unknown): boolean;
    /**
     * Creates adapter for given resource.
     *
     * @param resource
     */
    create(resource: ResourceType): ImageAdapter<ResourceType>;
}
