import type { ImageAdapter } from "../image-adapter";
import { Viewport, type ViewportLiteral } from "../pixel-accessor";
import type { ReversePixelMapper } from "../pixel-mapper";
/**
 * Output image viewport resolver interface.
 */
export interface OutputViewportResolver {
    /**
     * Resolves viewport for output image, based on given source image, reverse pixel mapper and viewport option value.
     *
     * @param image
     * @param reversePixelMapper
     * @param viewport
     */
    resolve(image: ImageAdapter<unknown>, reversePixelMapper: ReversePixelMapper, viewport: "bestFit" | boolean | ViewportLiteral): Viewport;
}
