import { Viewport, type ViewportLiteral } from "../pixel-accessor";
import type { ReversePixelMapper } from "../pixel-mapper";
import type { ImageAdapter } from "../image-adapter";
/**
 * Output image viewport resolution strategy.
 */
export interface OutputViewportStrategy {
    /**
     * Priority of strategy matching.
     */
    readonly priority: number;
    /**
     * Returns true if current strategy can handle output viewport resolution for given ReversePixelMapper and
     * source image viewport.
     *
     * @param reversePixelMapper
     * @param viewport
     */
    match(reversePixelMapper: ReversePixelMapper, viewport: "bestFit" | boolean | ViewportLiteral): boolean;
    /**
     * Returns viewport for output image.
     *
     * @param image
     * @param reversePixelMapper
     * @param viewport
     */
    getOutputViewport(image: ImageAdapter<unknown>, reversePixelMapper: ReversePixelMapper, viewport: "bestFit" | boolean | ViewportLiteral): Viewport;
}
