import type { HasAverageColor } from "./HasAverageColor";
import type { HasBackgroundColor } from "./HasBackgroundColor";
import type { VirtualPixelDecorator } from "./virtual-pixel-decorator";
import type { VirtualPixelMethod } from "./VirtualPixelMethod";
import type { VirtualViewportPixelAccessor } from "./VirtualViewportPixelAccessor";
/**
 * Virtual pixel decorator applicator interface.
 */
export interface VirtualPixelDecoratorApplicator {
    /**
     * Decorates given interface by virtual pixel method decorator.
     *
     * @param decorated
     * @param method
     */
    decorate<DecoratedType extends VirtualViewportPixelAccessor & HasAverageColor & HasBackgroundColor>(decorated: DecoratedType, method: VirtualPixelMethod): VirtualPixelDecorator<DecoratedType>;
}
