import type { Color } from "../../types";
import type { HasAverageColor } from "../HasAverageColor";
import type { HasBackgroundColor } from "../HasBackgroundColor";
import type { VirtualViewportPixelAccessor } from "../VirtualViewportPixelAccessor";
/**
 * Virtual pixel decorator interface.
 */
export interface VirtualPixelDecorator<DecoratedType extends VirtualViewportPixelAccessor & HasAverageColor & HasBackgroundColor> extends VirtualViewportPixelAccessor, HasAverageColor, HasBackgroundColor {
    /**
     * Returns virtual pixel color.
     *
     * @param x
     * @param y
     */
    getVirtualPixelColor(x: number, y: number): Color;
    /**
     * Returns decorated instance.
     */
    getDecorated(): DecoratedType;
}
