/**
 * Blends two colors by given balance. Balance should be number between 0 and 1.
 * Balance is weight of first color. Second color weight is (1 - balance).
 *
 * @param color1
 * @param color2
 * @param balance
 */
export declare function blendColors(color1: Color, color2: Color, balance?: number): Color;
