/**
 * Deferred object.
 */
export type Deferred<T> = {
    /**
     * Resolves deferred promise with given value.
     * @param value
     */
    resolve(value: T): void;
    /**
     * Rejects deferred promise with given reason.
     * @param reason
     */
    reject(reason?: Error | any): void;
    /**
     * Deferred promise.
     */
    promise: Promise<T>;
};
/**
 * Creates Deferred object.
 */
export declare function createDeferred<T>(): Deferred<T>;
